import React, { useState, useEffect } from 'react';
import './HeroSection.css';

const HeroSection: React.FC = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(true);

  // Array of hero images (you can add more as needed)
  const heroImages = [
    '/hero-1.jpg',
    '/hero-2.jpg',
    '/hero-3.jpg',
    '/hero-4.jpg'
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      // Start fade out
      setIsVisible(false);
      
      // After fade out completes, change image and fade in
      setTimeout(() => {
        setCurrentImageIndex((prevIndex) => 
          (prevIndex + 1) % heroImages.length
        );
        setIsVisible(true);
      }, 500); // Half of the transition duration
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval);
  }, [heroImages.length]);

  return (
    <section className="hero-section">
      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">
            Create your will today â€” simple, secure, and stress-free.
          </h1>
          <p className="hero-subtitle">
            Ensure your peace of mind and your family's future with our easy-to-use will creation service.
          </p>
        </div>
        <div className="hero-illustration">
          <div className="hero-image-container">
            <img 
              src={heroImages[currentImageIndex]} 
              alt={`Hero image ${currentImageIndex + 1}`}
              className={`hero-image ${isVisible ? 'fade-in' : 'fade-out'}`}
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
